/action {per-round damage of %0} {/var ItemAvg $0} {Item_ID}
/action {per-round damage of %0.0} {/var ItemAvg $0} {Item_ID}
/action {^Durability: %0 Condition: %1} {/var ItemDurability $0;/var ItemCondition $1} {Item_ID}
/alias {fidb} {getid;f $BriefID} {Item_ID}
/alias {finditemal} {/empty ItemAlign;/loop {0,@Math(@Len($ItemEffects)-1)} {/if {"@Mid($ItemEffects,$LoopCount,1)"="-"} {/var Temp @Mid($ItemEffects,@Math($LoopCount+1),1);/var ItemAlign @ConCat($ItemAlign,A$Temp )}}} {Item_ID}
/alias {finditemnots} {/empty ItemNots;/if {@IsEmpty($ItemAttr)=0} {/loop {0,@Math(@Len($ItemAttr)-1)} {/if {"@Mid($ItemAttr,$LoopCount,1)"="_"} {/var Temp @Mid($ItemAttr,@Math($LoopCount+1),1);/var {Tempo} {H};/if {$Temp=$Tempo} {/var ItemNots @ConCat($ItemNots,2$Temp )} {/var ItemNots @ConCat($ItemNots,!$Temp )}}}}} {Item_ID}
/alias {getid} {/var BriefID $ObjectName:;/if {$ItemLevel != 0} {/var BriefID @ConCat($BriefID, Lev($ItemLevel))};/var BriefID @ConCat($BriefID, Loc/Type(@Lower(@RTrim($ItemEquip))));/if {@IsEmpty($ItemLight)=0} {/var BriefID @ConCat($BriefID, Hours($ItemLight))};/if {@IsEmpty($ItemMaxWeight)=0} {/var BriefID @ConCat($BriefID, Holds($ItemMaxWeight))};/if {@IsEmpty($ItemTreasure)=0} {/var BriefID @ConCat($BriefID, Value($ItemValue))} ;/if {@IsEmpty($ItemPot)=0} {/var BriefID @ConCat($BriefID, Casts($ItemCast))};/if {@IsEmpty($ItemDice)=0} {/var BriefID @ConCat($BriefID, Dice($ItemDice))};/if {@IsEmpty($ItemAvg)=0} {/var BriefID @ConCat($BriefID, Avg($ItemAvg))};/if {@IsEmpty($ItemSpell)=0} {/var BriefID @ConCat($BriefID, Casts($ItemSpell))};/if {@IsEmpty($ItemSpell)=0} {/var BriefID @ConCat($BriefID, Charges($ItemCurCharges of $ItemTotalCharges))};/if {@IsEmpty($ItemWandCasts)=0} {/var BriefID @ConCat($BriefID, Casts($ItemWandCasts))};/if {@IsEmpty($ItemWandCasts)=0} {/var BriefID @ConCat($BriefID, Charges($ItemCurCharges of $ItemTotalCharges))};/if {$ItemAffectCount > 0} {/loop {1,$ItemAffectCount} {/var BriefID {@ConCat($BriefID, @Var(@ConCat(ItemAffect,@ConCat($LoopCount,Stat)))(@Var(@ConCat(ItemAffect,@ConCat($LoopCount,Num)))))}}};finditemnots;/if {@IsEmpty($ItemNots) = 0} {/var BriefID @ConCat($BriefID, @RTrim($ItemNots))};finditemal;/if {@IsEmpty($ItemAlign) != 1} {/var BriefID @ConCat($BriefID, @RTrim($ItemAlign))};/if {@IsEmpty($ItemIs)=0} {/var BriefID @ConCat($BriefID, Specs($ItemIs))};/if {@IsEmpty($ItemAttr)=0} {/var BriefID @ConCat($BriefID, Class Restrictions($ItemAttr))};/if {@IsEmpty($ItemLag)=0} {/var BriefID @ConCat($BriefID, Rounds of Lag($ItemLag))};/if {@IsEmpty($ItemDurability)=0} {/var BriefID @ConCat($BriefID, Durability $ItemDurability)};/if {@IsEmpty($ItemCondition)=0} {/var BriefID @ConCat($BriefID, Condition $ItemCondition)};/if {@IsEmpty($ItemKeys)=0} {/var BriefID @ConCat($BriefID, Holds($ItemKeys))}} {Item_ID}
/alias {idshout} {getid; shout $BriefID} {Item_ID}
/alias {idauc} {getid;auction $BriefID} {Item_ID}
/alias {idch} {getid;ch $BriefID} {Item_ID}
/alias {idclan} {getid;clan $BriefID} {Item_ID}
/alias {idform} {getid;f $BriefID} {Item_ID}
/alias {idgos} {getid;gos $BriefID} {Item_ID}
/alias {idgui} {getid;gui $BriefID} {Item_ID}
/alias {idhelp} {/showme {@Chr(10)@AnsiReset()@AnsiBold()@ForeCyan()Commands         @ForeBlue()- - - - - - - - -  @ForeCyan()What they do... @Chr(10)@ForeBlue()---------------------------------------------------------------------------------------------@Chr(10)@ForeCyan()Idhelp           @ForeBlue()- - - - - - - - -  @ForeCyan()This listing@Chr(10)IdCh             @ForeBlue()- - - - - - - - -  @ForeCyan()Displays ID information to Chat Sessions -MAY NOT WORK- @Chr(10)Idgos            @ForeBlue()- - - - - - - - -  @ForeCyan()Displays ID information to Gossip Channel @Chr(10)Idwiz            @ForeBlue()- - - - - - - - -  @ForeCyan()Displays ID information to Wiz Channel @Chr(10)Idclan           @ForeBlue()- - - - - - - - -  @ForeCyan()Displays ID information to Clan Channel @Chr(10)Idgui            @ForeBlue()- - - - - - - - -  @ForeCyan()Displays ID information to Guild Channel @Chr(10)Idform           @ForeBlue()- - - - - - - - -  @ForeCyan()Displays ID information to FormTalk @Chr(10)Idme             @ForeBlue()- - - - - - - - -  @ForeCyan()Displays ID information to Your screen @Chr(10)Idauc            @ForeBlue()- - - - - - - - -  @ForeCyan()Displays ID information to Auction Channel @Chr(10)Idtell <person>  @ForeBlue()- - - - - - - - -  @ForeCyan()Tells ID information to <person> @Chr(10)Idsay            @ForeBlue()- - - - - - - - -  @ForeCyan()Says ID information people in your room @Chr(10)Idshout          @ForeBlue()- - - - - - - - -  @ForeCyan()Shouts ID information to people in your zone @Chr(10)Itemidon         @ForeBlue()- - - - - - - - -  @ForeCyan()Enable  Item ID @Chr(10)Itemidoff        @ForeBlue()- - - - - - - - -  @ForeCyan()Disable Item ID @Chr(10)Idabout          @ForeBlue()- - - - - - - - -  @ForeCyan()Displays information about ItemID @Chr(10)@AnsiReset()}} {Item_ID}
/alias {idme} {getid;/showme {@Chr(10)@AnsiReset()@AnsiBold()@ForeMagenta() $BriefID @AnsiReset()}} {Item_ID}
/alias {idsay} {getid;say $BriefID} {Item_ID}
/alias {idtell %0} {getid;tell $0 $BriefID} {Item_ID}
/alias {idwiz} {getid;wiz $BriefID} {Item_ID}
/alias {listlookup} {/loop {1,$AffectLookup} {/if {@IsEmpty(@GetArray(Affects,$LoopCount,1))=0} {room $LoopCount: @GetArray(Affects,$LoopCount,1),@GetArray(Affects,$LoopCount,2)}}} {Item_ID}
/alias {lookupaff %0} {/empty Temp;/loop {1,$AffectLookup} {/if {@IsEmpty(@GetArray(Affects,$LoopCount,1))=0} {/if {"@GetArray(Affects,$LoopCount,1)" = $0} {/varTemp @GetArray(Affects,$LoopCount,2)}}};/if {@IsEmpty($Temp)}{/var Temp $0}} {Item_ID}
/alias {teidb %0} {/var BriefID {$ObjectName:};/if {$ItemLevel != 0} {/var BriefID @ConCat($BriefID, Lev($ItemLevel))};/var BriefID @ConCat($BriefID, Loc(@Lower(@RTrim($ItemEquip))));/if {@IsEmpty($ItemAC)=0} {/var BriefID @ConCat($BriefID, Ac($ItemAC))};/if {@IsEmpty($ItemDice)=0} {/var BriefID @ConCat($BriefID, $ItemDice)};/if {@IsEmpty($ItemSpell)=0} {/var BriefID @ConCat($BriefID, Spell($ItemSpell));/var BriefID @ConCat($BriefID, Lvl($ItemSpellLevel));/var BriefID @ConCat($BriefID, Charges($ItemCurCharges of $ItemTotalCharges))};finditemnots;/if {@IsEmpty($ItemNots) = 0} {/var BriefID @ConCat($BriefID, @RTrim($ItemNots))};finditemal;/if {@IsEmpty($ItemAlign) != 1} {/var BriefID @ConCat($BriefID, @RTrim($ItemAlign))};/var BriefID @ConCat($BriefID, Cond($ItemCondition));te $0 $BriefID} {Item_ID}
/alias {idabout} {/showme {@Chr(10)@AnsiReset()@AnsiBold()@ForeCyan()Item ID @Chr(10)Created By Cypher! @Chr(10)Version 1.1 @Chr(10)May 21, 2002 @Chr(10)@AnsiReset()}} {Item_ID}
/alias {itemidoff} {/disablegroup Item_ID;/showme {@Chr(10)@AnsiReset()@AnsiBold()@ForeCyan()Item ID is Now @ForeRed()OFF@AnsiReset()}} {Item_IDConfig}
/alias {itemidon} {/enablegroup Item_ID;/showme {@Chr(10)@AnsiReset()@AnsiBold()@ForeCyan()Item ID is Now @ForeRed()ON@AnsiReset()}} {Item_IDConfig}
